function labelFor(course){
  const cr = (course.credits === null || course.credits === undefined) ? "—" : String(course.credits);
  return `${course.code}\n${course.name}\nCréditos: ${cr}`;
}

function presentSet(){
  return new Set(COURSES.map(c => c.code));
}

function buildEdges(){
  const present = presentSet();
  const edges = [];
  for (const c of COURSES){
    const preds = Array.isArray(c.predecessors) ? c.predecessors : [];
    for (const p of preds){
      if (!present.has(p)) continue;
      edges.push({ from: p, to: c.code, arrows: "to" });
    }
  }
  return edges;
}

function computeGroups() {
  const present = presentSet();
  const adj = new Map([...present].map(k => [k, []]));
  const edges = buildEdges();
  for (const e of edges) adj.get(e.from).push(e.to);

  const dist = new Map();
  function bfsFromRoot(root){
    const q = [root];
    dist.set(`${root}|${root}`, 0);
    while (q.length){
      const u = q.shift();
      const du = dist.get(`${root}|${u}`);
      for (const v of adj.get(u) || []){
        const key = `${root}|${v}`;
        if (!dist.has(key)){
          dist.set(key, du + 1);
          q.push(v);
        }
      }
    }
  }

  for (const r of ROOTS) if (present.has(r)) bfsFromRoot(r);

  const groupOf = new Map();
  const levelOf = new Map();

  for (const code of present){
    let bestRoot = null;
    let bestDist = Infinity;
    let tie = false;

    for (const r of ROOTS){
      const d = dist.get(`${r}|${code}`);
      if (d === undefined) continue;
      if (d < bestDist) {
        bestDist = d; bestRoot = r; tie = false;
      } else if (d === bestDist) {
        tie = true;
      }
    }

    if (!bestRoot) {
      groupOf.set(code, "OTROS");
      levelOf.set(code, 0);
    } else if (tie && code !== bestRoot) {
      groupOf.set(code, "MULTI");
      levelOf.set(code, bestDist);
    } else {
      groupOf.set(code, bestRoot);
      levelOf.set(code, bestDist);
    }
  }

  return { groupOf, levelOf };
}

